import { fetch } from "Profisee";

const ASSET_TYPE_ID_CODE_VALUE = "00000000-0000-0000-0000-000000021001";
const ASSET_TYPE_ID_CODE_SET   = "00000000-0000-0000-0000-000000021002";

const RELATION_TYPE_ID_CODEVALUE_IN_CODESET = "00000000-0000-0000-0000-000000007041";

const ATTRIBUTE_TYPE_ID_DESCRIPTION = "00000000-0000-0000-0000-000000003114";
const ATTRIBUTE_TYPE_ID_LOCATION = "00000000-0000-0000-0000-000000000203";

const ATTRIBUTE_TYPE_NAME_SOURCE = "Source";
const ATTRIBUTE_TYPE_NAME_ID = "Id"

function utf8Bytes(str) {
  const bytes = [];
  for (let i = 0; i < str.length; i++) {
    let codePoint = str.charCodeAt(i);
    if (codePoint >= 0xD800 && codePoint <= 0xDBFF && i + 1 < str.length) {
      const next = str.charCodeAt(i + 1);
      if (next >= 0xDC00 && next <= 0xDFFF) {
        codePoint = 0x10000 + ((codePoint - 0xD800) << 10) + (next - 0xDC00);
        i++;
      }
    }
    if (codePoint <= 0x7F) {
      bytes.push(codePoint);
    } else if (codePoint <= 0x7FF) {
      bytes.push(0xC0 | (codePoint >> 6));
      bytes.push(0x80 | (codePoint & 0x3F));
    } else if (codePoint <= 0xFFFF) {
      bytes.push(0xE0 | (codePoint >> 12));
      bytes.push(0x80 | ((codePoint >> 6) & 0x3F));
      bytes.push(0x80 | (codePoint & 0x3F));
    } else {
      bytes.push(0xF0 | (codePoint >> 18));
      bytes.push(0x80 | ((codePoint >> 12) & 0x3F));
      bytes.push(0x80 | ((codePoint >> 6) & 0x3F));
      bytes.push(0x80 | (codePoint & 0x3F));
    }
  }
  return bytes;
}

function base64FromBytes(bytes) {
  const chars = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789+/";
  let out = "";
  for (let i = 0; i < bytes.length; i += 3) {
    const b1 = bytes[i];
    const b2 = i + 1 < bytes.length ? bytes[i + 1] : 0;
    const b3 = i + 2 < bytes.length ? bytes[i + 2] : 0;
    const triplet = (b1 << 16) | (b2 << 8) | b3;
    const c1 = (triplet >> 18) & 0x3F;
    const c2 = (triplet >> 12) & 0x3F;
    const c3 = (triplet >> 6) & 0x3F;
    const c4 = triplet & 0x3F;
    out += chars.charAt(c1);
    out += chars.charAt(c2);
    out += (i + 1 < bytes.length) ? chars.charAt(c3) : "=";
    out += (i + 2 < bytes.length) ? chars.charAt(c4) : "=";
  }
  return out;
}

function b64EncodeUtf8(str) {
  return base64FromBytes(utf8Bytes(str));
}

function normalizeBaseUrl(baseUrl) {
  if (!baseUrl) return "";
  return baseUrl.endsWith("/") ? baseUrl.slice(0, -1) : baseUrl;
}

function parseJsonSafe(body) {
  if (!body) return null;
  try { return JSON.parse(body); } catch { return { raw: body }; }
}

function httpJson(baseUrl, authHeader, method, path, bodyObj) {
  const url = `${baseUrl}${path}`;
  const options = {
    method,
    headers: {
      "Authorization": authHeader,
      "Accept": "application/json",
      "Content-Type": "application/json"
    }
  };
  if (bodyObj !== undefined && bodyObj !== null) {
    options.body = JSON.stringify(bodyObj);
  }

  const res = fetch(url, options);

  // Network exception (Connect fetch captures exceptions on the response object)
  if (res.exceptionType) {
    return {
      ok: false,
      status: res.status || 0,
      error: res.error || `Request failed calling ${method} ${path}`,
      body: res.body || "",
      json: null
    };
  }

  const json = parseJsonSafe(res.body);
  if (!res.ok) {
    return {
      ok: false,
      status: res.status,
      error: `HTTP ${res.status} calling ${method} ${path}: ${res.body}`,
      body: res.body || "",
      json
    };
  }

  return { ok: true, status: res.status, error: "", body: res.body || "", json };
}

function findFirstId(resultsContainer) {
  return resultsContainer && resultsContainer.results && resultsContainer.results.length > 0
    ? resultsContainer.results[0].id
    : null;
}

function getCommunityId(baseUrl, authHeader, communityName) {
  const r = httpJson(baseUrl, authHeader, "GET",
    `/rest/2.0/communities?name=${encodeURIComponent(communityName)}&nameMatchMode=EXACT`);
  if (!r.ok) throw new Error(r.error);
  return findFirstId(r.json);
}

function getDomainId(baseUrl, authHeader, communityId, domainName) {
  const r = httpJson(baseUrl, authHeader, "GET",
    `/rest/2.0/domains?communityId=${encodeURIComponent(communityId)}&name=${encodeURIComponent(domainName)}&nameMatchMode=EXACT`);
  if (!r.ok) throw new Error(r.error);
  return findFirstId(r.json);
}

function createDomain(baseUrl, authHeader, communityId, domainName) {
  const rType = httpJson(
    baseUrl,
    authHeader,
    "GET",
    `/rest/2.0/domains?communityId=${encodeURIComponent(communityId)}&limit=1`
  );
  if (!rType.ok) throw new Error(rType.error);

  const first =
    rType.json &&
    rType.json.results &&
    rType.json.results.length > 0
      ? rType.json.results[0]
      : null;

  const typeId = first && first.type && first.type.id ? first.type.id : null;
  if (!typeId) {
    throw new Error("Could not determine domain typeId from existing domains in community");
  }

  const r = httpJson(
    baseUrl,
    authHeader,
    "POST",
    `/rest/2.0/domains`,
    { name: domainName, typeId, communityId }
  );
  if (!r.ok) throw new Error(r.error);
  return r.json;
}

function findAssetIdInDomain(baseUrl, authHeader, domainId, assetName) {
  const r = httpJson(baseUrl, authHeader, "GET",
    `/rest/2.0/assets?domainId=${encodeURIComponent(domainId)}&name=${encodeURIComponent(assetName)}&nameMatchMode=EXACT`);
  if (!r.ok) throw new Error(r.error);
  return findFirstId(r.json);
}

function createAsset(baseUrl, authHeader, name, displayName, domainId, typeId) {
  const r = httpJson(baseUrl, authHeader, "POST", `/rest/2.0/assets`, { name: name, displayName: displayName, typeId: typeId, domainId: domainId });
  if (!r.ok) throw new Error(r.error);
  return r.json;
}

function findRelationId(baseUrl, authHeader, typeId, sourceId, targetId) {
  const r = httpJson(
    baseUrl,
    authHeader,
    "GET",
    `/rest/2.0/relations?typeId=${encodeURIComponent(typeId)}&sourceId=${encodeURIComponent(sourceId)}&targetId=${encodeURIComponent(targetId)}`
  );
  if (!r.ok) throw new Error(r.error);
  return findFirstId(r.json);
}

function createRelation(baseUrl, authHeader, typeId, sourceId, targetId) {
  const r = httpJson(baseUrl, authHeader, "POST", `/rest/2.0/relations`, { typeId, sourceId, targetId });
  if (!r.ok) throw new Error(r.error);
  return r.json;
}

function findAttributeId(baseUrl, authHeader, assetId, attrTypeId) {
  const r = httpJson(
    baseUrl,
    authHeader,
    "GET",
    `/rest/2.0/attributes?assetId=${encodeURIComponent(assetId)}&typeIds=${encodeURIComponent(attrTypeId)}`
  );
  if (!r.ok) throw new Error(r.error);
  return findFirstId(r.json);
}

function createAttribute(baseUrl, authHeader, assetId, typeId, value) {
  const r = httpJson(baseUrl, authHeader, "POST", `/rest/2.0/attributes`, { assetId, typeId, value });
  if (!r.ok) throw new Error(r.error);
  return r.json;
}

function updateAttribute(baseUrl, authHeader, attributeId, value) {
  const r = httpJson(baseUrl, authHeader, "PATCH", `/rest/2.0/attributes/${encodeURIComponent(attributeId)}`, { value });
  if (!r.ok) throw new Error(r.error);
  return r.json;
}

function upsertAttribute(baseUrl, authHeader, assetId, attrTypeId, value) {
  if (!attrTypeId || value === undefined || value === null || value === "") return;
  const attrId = findAttributeId(baseUrl, authHeader, assetId, attrTypeId);
  if (attrId) {
    updateAttribute(baseUrl, authHeader, attrId, value);
  } else {
    createAttribute(baseUrl, authHeader, assetId, attrTypeId, value);
  }
}

function getAttributeTypeIdByName(baseUrl, authHeader, attributeTypeName) {
  if (!attributeTypeName) return "";

  const r = httpJson(
    baseUrl,
    authHeader,
    "GET",
    `/rest/2.0/attributeTypes/name/${encodeURIComponent(attributeTypeName)}`
  );

  if (!r.ok) throw new Error(r.error);

  return r.json && r.json.id ? r.json.id : "";
}

export function execute(input) {
  const log = [];
  const step = (m) => { try { log.push(String(m)); } catch { } };

  try {
    const records = (input && input.records) ? input.records : [];
    step(`records.length=${records.length}`);

    if (!records || records.length === 0) {
      throw("No records");
    }

    const baseUrl = normalizeBaseUrl(input.baseUrl);
    const username = input.username;
    const password = input.password.configuration.value;

    step(`baseUrl=${baseUrl}`);

    step("Auth header");
    const rawBasic = String(username) + ":" + String(password);
    const encodedBasic = b64EncodeUtf8(rawBasic);
    const authHeader = "Basic " + encodedBasic;

    step("Resolve attribute type IDs");
    let sourceAttrTypeId = "";
    sourceAttrTypeId = getAttributeTypeIdByName(baseUrl, authHeader, ATTRIBUTE_TYPE_NAME_SOURCE);
    step(`sourceAttrTypeId=${sourceAttrTypeId}`);
    if (!sourceAttrTypeId) throw(`Attribute type not found: ${ATTRIBUTE_TYPE_NAME_SOURCE}`);

    let codeSetIdAttributeTypeId = "";
    codeSetIdAttributeTypeId = getAttributeTypeIdByName(baseUrl, authHeader, ATTRIBUTE_TYPE_NAME_ID);
    step(`codeSetIdAttributeTypeId=${codeSetIdAttributeTypeId || "(empty)"}`);

    const communityIds = {}; // communityName -> communityId
    const domainIds = {};    // `${communityId}::${domainName}` -> domainId
    const codeSetIds = {};   // `${domainId}::${codeSetName}` -> codeSetId

    const outputRecords = [];

    for (let i = 0; i < records.length; i++) {
      const r = records[i] || {};
      const codeValueName = r.codeValueName;
      const codeValueDisplayName = r.codeValueDisplayName;
      const description = r.description;
      const location = r.location;
      const communityName = r.communityName;
      const domainName = r.domainName;
      const codeSetName = r.codeSetName;
      const source = r.source;
      const codeSetIdAttributeValue = r.codeSetId;

      step(`row ${i} start`);

      try {
        if (!communityName) throw(`communityName is required (row ${i})`);
        if (!domainName) throw(`domainName is required (row ${i})`);
        if (!codeSetName) throw(`codeSetName is required (row ${i})`);

        let communityId = communityIds[communityName];
        if (!communityId) {
          step(`row ${i} Get communityId for communityName='${communityName}'`);
          communityId = getCommunityId(baseUrl, authHeader, communityName);
          step(`row ${i} communityId=${communityId}`);
          if (!communityId) throw(`Community not found: ${communityName}`);
          communityIds[communityName] = communityId;
        }

        const domainKey = `${communityId}::${domainName}`;
        let domainId = domainIds[domainKey];
        if (!domainId) {
          step(`row ${i} Get domainId for domainName='${domainName}'`);
          domainId = getDomainId(baseUrl, authHeader, communityId, domainName);
          step(`row ${i} domainId=${domainId}`);
          if (!domainId) {
            step(`row ${i} Domain not found; creating Domain`);
            const createdDomain = createDomain(baseUrl, authHeader, communityId, domainName);
            domainId = createdDomain && createdDomain.id ? createdDomain.id : "";
            step(`row ${i} created domainId=${domainId}`);
            if (!domainId) throw(`Failed to create Domain: ${domainName}`);
          }
          domainIds[domainKey] = domainId;
        }

        const codeSetKey = `${codeSetName}`;
        let codeSetId = codeSetIds[codeSetKey];
        if (!codeSetId) {
          step(`row ${i} Get codeSetId for codeSetName='${codeSetName}'`);
          codeSetId = findAssetIdInDomain(baseUrl, authHeader, domainId, codeSetName);
          step(`row ${i} codeSetId=${codeSetId}`);
          if (!codeSetId) {
            step(`row ${i} Code Set not found; creating Code Set asset`);
            const createdCodeSet = createAsset(baseUrl, authHeader, codeSetName, codeSetName, domainId, ASSET_TYPE_ID_CODE_SET);
            codeSetId = createdCodeSet && createdCodeSet.id ? createdCodeSet.id : "";
            step(`row ${i} created codeSetId=${codeSetId}`);
            if (!codeSetId) throw(`Failed to create Code Set asset: ${codeSetName}`);

            // Only when the Code Set is newly created do we set its attributes
            if (source) {
              step(`row ${i} Insert source attribute on code set ${codeSetId}`);
              upsertAttribute(baseUrl, authHeader, codeSetId, sourceAttrTypeId, source);
              step(`row ${i} code set ${codeSetId} source attribute upserted`);
            }

            if (codeSetIdAttributeValue) {
              if (!codeSetIdAttributeTypeId) {
                throw(`Attribute type not found: ${ATTRIBUTE_TYPE_NAME_ID}`);
              }
              step(`row ${i} Insert Code Set ID attribute on code set ${codeSetId}`);
              upsertAttribute(baseUrl, authHeader, codeSetId, codeSetIdAttributeTypeId, codeSetIdAttributeValue);
              step(`row ${i} Code set ${codeSetId} ID attribute upserted`);
            }
          }
          codeSetIds[codeSetKey] = codeSetId;
        }

        step(`row ${i} codeValueName=${codeValueName}`);
        let codeValueId = findAssetIdInDomain(baseUrl, authHeader, domainId, codeValueName);
        step(`row ${i} existing codeValueId=${codeValueId}`);
        if (!codeValueId) {
          const created = createAsset(baseUrl, authHeader, codeValueName, codeValueDisplayName, domainId, ASSET_TYPE_ID_CODE_VALUE);
          codeValueId = created && created.id ? created.id : "";
          step(`row ${i} created codeValueId=${codeValueId}`);
        }
        if (!codeValueId) throw("Could not determine Code Value ID");

        // Relation: Code Value -> Code Set
        const relId = findRelationId(
          baseUrl,
          authHeader,
          RELATION_TYPE_ID_CODEVALUE_IN_CODESET,
          codeValueId,
          codeSetId
        );
        
        step(`row ${i} relId=${relId}`);
        if (!relId) {
          createRelation(baseUrl, authHeader, RELATION_TYPE_ID_CODEVALUE_IN_CODESET, codeValueId, codeSetId);
          step(`row ${i} relation created`);
        }

        if (description) {
          upsertAttribute(baseUrl, authHeader, codeValueId, ATTRIBUTE_TYPE_ID_DESCRIPTION, description);
          step(`row ${i} description attribute upserted`);
        }

        if (location) {
          upsertAttribute(baseUrl, authHeader, codeValueId, ATTRIBUTE_TYPE_ID_LOCATION, location);
          step(`row ${i} location attribute upserted`);
        }

        outputRecords.push({
          codeValueName: codeValueName,
          codeValueId: codeValueId,
          codeUrl: `${baseUrl}/asset/${codeValueId}`,
          log: log.join(" | ")
        });

      } catch (e) {
        const msg = e && e.message ? e.message : String(e);
        throw(`Row ${i} Error: ${msg}`);
      }
    }

    step("DONE");
    return { isSuccess: true, records: outputRecords };
  } catch (e) {
    const msg = e && e.message ? e.message : String(e);
    return { isSuccess: false, error: `ERROR: ${msg}/nLOG: ${log}` }
  }
}
